I,J,K=no_of_plants,no_of_plots,2

from qiskit.utils import algorithm_globals
from qiskit.algorithms.minimum_eigensolvers import QAOA, NumPyMinimumEigensolver
from qiskit.algorithms.optimizers import COBYLA
from qiskit.primitives import Sampler
from qiskit_optimization.algorithms import (
    MinimumEigenOptimizer,
    RecursiveMinimumEigenOptimizer,
    SolutionSample,
    OptimizationResultStatus,
)
from qiskit_optimization import QuadraticProgram
from qiskit.visualization import plot_histogram
from lol import PlantOpp
if st.button('Run'):
    f=PlantOpp(area,yd,soil,price,fertizer_need,impactFactor,fertilizer_price,Enviorment,I,J,K)
    print(f.find_liner_coffecent())
    print(f.find_quad_coffent()) 
    print(f.create_constraints())
    

    qubo = QuadraticProgram()
    qubo.binary_var_list(range(I*J*K))
    qubo.maximize(linear=f.find_liner_coffecent(),quadratic=f.find_quad_coffent())
    for stuff in f.create_constraints():
        qubo.linear_constraint(linear=stuff[0],sense=stuff[1][1],rhs=stuff[1][0])
    print(qubo.prettyprint())
    
    qubo_bad = QuadraticProgram()
    qubo_bad.binary_var_list(range(I*J*K))
    qubo_bad.minimize(linear=f.find_liner_coffecent(),quadratic=f.find_quad_coffent())

    from qiskit_optimization.converters import LinearEqualityToPenalty
    lineq2penalty = LinearEqualityToPenalty()
    qubo = lineq2penalty.convert(qubo)
    op, offset = qubo.to_ising()
    print("offset: {}".format(offset))
    print("operator:")
    print(op)
    
    lineq2penalty = LinearEqualityToPenalty()
    qubo_bad = lineq2penalty.convert(qubo_bad)
    op_bad, offset_bad = qubo_bad.to_ising()
    print("offset: {}".format(offset_bad))
    print("operator:")
    print(op_bad)

    algorithm_globals.random_seed = 10598
    qaoa_mes = QAOA(sampler=Sampler(), optimizer=COBYLA())#, initial_point=[0.0, 0.0])
    exact_mes = NumPyMinimumEigensolver()
    

    qaoa = MinimumEigenOptimizer(qaoa_mes)  # using QAOA
    exact = MinimumEigenOptimizer(exact_mes)
    if mode:
        exact_result = exact.solve(qubo)
